import React from "react"
import { useHistory, Link } from "react-router-dom";
import kanye from "../img/kanye.png"
import '../App.css'
import Confetti from "react-confetti"

class Home extends React.Component{
    constructor(props) {
        super(props);
        let randx = (Math.round(Math.random() * (window.innerWidth -50))).toString() + "px"
        console.log(randx)
        let randy = (Math.round(Math.random() * (window.innerHeight -50))).toString() + "px"
        this.state = { 
            windowWidth: window.innerWidth,
            windowHeight: window.innerHeight,
            kanyeStyle: {
                position: "absolute",
                left: randx,
                top: randy,
            },
            found: false
        };
      }

    foundKanye(){
        console.log("A wild kanye has been found.")
        this.setState({
            found: true
        })
    }

    render() {
         return(
                <div class="h-screen w-screen bg-background">
                    <a href="" class="kanyePosition" style={this.state.kanyeStyle} onClick={() => this.foundKanye}>
                        <img src={kanye} alt="" class="h-12 w-12 opacity-80"/>
                    </a>
                    <Confetti class={this.state.found ? "none" : ""}
                        width={this.state.windowWidth}
                        height={this.state.windowHeight}
                        tweenDuration={500}
                        run={this.state.run}
                    />
                </div>
            )
    }
}

export default Home
