import React from "react"
import { useHistory, Link } from "react-router-dom";
import kanye from "../img/kanye.png"
import '../App.css'

class Home extends React.Component{
    constructor(props) {
        super(props);
        this.state = { 
            windowWidth: window.innerWidth,
            windowHeight: window.innerHeight,
            kanyeStyle: null
        };
      }

    findCoords(){
        let randx = Math.random() * (this.state.windowWidth -50)
        let randy = Math.random() * (this.state.windowHeight -50)
        this.setState({
            kanyeStyle:{
                position: "absolute",
                left: randx.toString + "px",
                top: randy.toString + "px",
            }
        })
    }

    render() {
        if (this.state.kanyeStyle === null) {
            this.findCoords();
            return(
                <div class="h-screen w-screen bg-background">
                    <a href="" class="kanyePosition" style={this.state.kanyeStyle}>
                        <img src={kanye} alt="" class="h-12 w-16"/>
                    </a>
                </div>
            )

        } else {
            return(
                <div class="h-screen w-screen bg-background">
                    <a href="" class="kanyePosition" style={this.state.kanyeStyle}>
                        <img src={kanye} alt="" class="h-12 w-16"/>
                    </a>
                </div>
            )
        }

    }
}

export default Home
