import React from "react"
import { useHistory, Link } from "react-router-dom";
import kanye from "../img/kanye.png"
import '../App.css'
import Confetti from "react-dom-confetti"

class Home extends React.Component{
    constructor(props) {
        super(props);
        let randx = (Math.round(Math.random() * (window.innerWidth -50))).toString() + "px"
        console.log(randx)
        let randy = (Math.round(Math.random() * (window.innerHeight -50))).toString() + "px"
        this.state = { 
            windowWidth: window.innerWidth,
            windowHeight: window.innerHeight,
            kanyeStyle: {
                position: "absolute",
                left: randx,
                top: randy,
            },
            found: false
        };
      }

    foundKanye(){
        this.setState({
            found: true
        })
    }

    // findCoords(){
    //     let randx = Math.random() * (this.state.windowWidth -50)
    //     let randy = Math.random() * (this.state.windowHeight -50)
    //     this.setState({
    //         kanyeStyle:{
    //             position: "absolute",
    //             left: randx.toString + "px",
    //             top: randy.toString + "px",
    //         }
    //     })
    // }

    render() {
        const config = {
            angle: 90,
            spread: 360,
            startVelocity: 40,
            elementCount: "158",
            dragFriction: 0.12,
            duration: 3000,
            stagger: 3,
            width: "10px",
            height: "10px",
            perspective: "500px",
            colors: ["#a864fd", "#29cdff", "#78ff44", "#ff718d", "#fdff6a"]
            };
        if (this.state.kanyeStyle === null) {
            this.findCoords();
            return(
                <div class="h-screen w-screen bg-background">
                    <a href="" class="kanyePosition" style={this.state.kanyeStyle} onClick={foundKanye}>
                        <img src={kanye} alt="" class="h-12 w-16 opacity-80"/>
                    </a>
                    <Confetti active={ this.state.found } config={ config }/>
                </div>
            )

        } else {
            const config = {
                angle: 90,
                spread: 360,
                startVelocity: 40,
                elementCount: "158",
                dragFriction: 0.12,
                duration: 3000,
                stagger: 3,
                width: "10px",
                height: "10px",
                perspective: "500px",
                colors: ["#a864fd", "#29cdff", "#78ff44", "#ff718d", "#fdff6a"]
                };
            return(
                <div class="h-screen w-screen bg-background">
                    <a href="" class="kanyePosition" style={this.state.kanyeStyle} onClick={foundKanye}>
                        <img src={kanye} alt="" class="h-12 w-12 opacity-80"/>
                    </a>

                <Confetti active={ this.state.found } config={ config }/>
                </div>
            )
        }

    }
}

export default Home
