import React from "react"
import { useHistory, Link } from "react-router-dom";
import kanye from "../img/kanye.png"
import '../App.css'

class Home extends React.Component{
    constructor(props) {
        super(props);
        console.log("loading again")
        let randx = (Math.round(Math.random() * (window.innerWidth -50))).toString() + "px"
        let randy = (Math.round(Math.random() * (window.innerHeight -50))).toString() + "px"
        this.state = { 
            windowWidth: window.innerWidth,
            windowHeight: window.innerHeight,
            kanyeStyle: {
                position: "absolute",
                left: randx,
                top: randy,
            },
            found: false
        };
      }

    foundKanye(){
        console.log("A wild kanye has been found.")
        this.setState({
            found: true
        })
    }

    render() {
        if (this.state.found === false) {
            return(
                <div class="h-screen w-screen bg-background">
                    <a href="" class="kanyePosition" style={this.state.kanyeStyle} onClick={() => this.foundKanye}>
                        <img src={kanye} alt="" class="h-12 w-12 opacity-80"/>
                    </a>
                </div>
            )
        } else {
            return(
                <div class="h-screen w-screen">
                    <div className="drop-shadow-md">
                        <p className="text-heading">Well done, you got it!</p>
                        <p className="">Do you want to play again?</p>
                        <div className="inline">
                            <button className="bg-green-600 text-white border-2 rounded border-green-600 p-10 py-6 hover:bg-white hover:text-green-600 mr-2">Yes</button>
                            <button className="bg-red-600 text-white border-2 rounded border-red-600 p-10 py-6 hover:bg-white hover:text-red-600">No</button>
                        </div>
                    </div>
                </div>
            )
        }

    }
}

export default Home
